import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import PageDefault from '../../../components/PageDefault';

function CadastroCategoria(){
  const [categorias, setCategorias] = useState(['teste'])

  const valoresIniciais = {
    nome: 'categoria inicial',
    descricao:'descrição inicial',
    cor: '#000',
  }

  const [values, setValues] = useState(valoresIniciais);


  return(
    <PageDefault>
     <h1>Cadastro de Categoria: {nomeDaCategoria}</h1>

      <form style={{ background: nomeDaCategoria }} onSubmit={function handleSubmit(infoDosEventos){
        infoDosEventos.preventDefault();
        setCategorias([
          ...categorias, 
          nomeDaCategoria
        ]);
      }}>

        <div>
          <label>
            Nome da Categoria:
            <input 
              type="text"
              value={values.nome}
              onChange={function funcaoHandler(infoDosEventos){
                setNomeDaCategoria(infoDosEventos.target.value);

              }}
            />
          </label>
        </div>

        <div>
          <label>
            Descrição:
            <textarea 
              type="text"
              value={value}
              onChange={function funcaoHandler(infoDosEventos){
                setNomeDaCategoria(infoDosEventos.target.value);

              }}
            />
          </label>

        </div>

        <div>
          <label>
            Cor:
            <input 
              type="color"
              value={nomeDaCategoria}
              onChange={function funcaoHandler(infoDosEventos){
                setNomeDaCategoria(infoDosEventos.target.value);

              }}
            />
          </label>

        </div>

        <button>
          Cadastrar
        </button>

        <ul>
          {categorias.map((categoria, indice) => {
           return (
             <li key={`${categoria}${indice}`}>
                {categoria}
              </li>

            ) 

          })}
        </ul>

      </form>

      <Link to="/">
        Ir para home
      </Link>
    </PageDefault>
  )
}

export default CadastroCategoria;
