import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import PageDefault from '../../../components/PageDefault';

function CadastroCategoria(){
  const [categorias, setCategorias] = useState(['teste'])

  const valoresIniciais = {
    nome: 'categoria inicial',
    descricao:'descrição inicial',
    cor: '#000',
  }

  const [values, setValues] = useState(valoresIniciais);

  function setValue(chave, valor) {
    setValues({
      ...values,
      [chave]: valor,

    })
  }

  return(
    <PageDefault>
     <h1>Cadastro de Categoria: {values.nome}</h1>

      <form onSubmit={function handleSubmit(infoDosEventos){
        infoDosEventos.preventDefault();
        setCategorias([
          ...categorias, 
          values
        ]);
      }}>

        <div>
          <label>
            Nome da Categoria:
            <input 
              type="text"
              name="nome"
              value={values.nome}
              onChange={function funcaoHandler(infoDosEventos){
                setValue(
                  infoDosEventos.target.getAttribute('name'), 
                  infoDosEventos.target.value);                
              }}
            />
          </label>
        </div>

        <div>
          <label>
            Descrição:
            <textarea 
              type="text"
              value={values.descricao}
              onChange={function funcaoHandler(infoDosEventos){
               // setNomeDaCategoria(infoDosEventos.target.value);

              }}
            />
          </label>

        </div>

        <div>
          <label>
            Cor:
            <input 
              type="color"
              value={values.cor}
              onChange={function funcaoHandler(infoDosEventos){
               // setNomeDaCategoria(infoDosEventos.target.value);

              }}
            />
          </label>

        </div>

        <button>
          Cadastrar
        </button>

        <ul>
          {categorias.map((categoria, indice) => {
           return (
             <li key={`${categoria}${indice}`}>
                {categoria}
              </li>

            ) 

          })}
        </ul>

      </form>

      <Link to="/">
        Ir para home
      </Link>
    </PageDefault>
  )
}

export default CadastroCategoria;
